var link=document.querySelector(".button-modal"),popup=document.querySelector(".modal-write-us"),close=popup.querySelector(".modal-close"),login=popup.querySelector("[name=name]"),password=popup.querySelector("[name=mail]"),form=popup.querySelector(".form");link.addEventListener("click",function(o){o.preventDefault(),popup.classList.add("modal-show"),login.focus()}),close.addEventListener("click",function(o){o.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(o){login.value&&password.value?localStorage.setItem("login",login.value):(o.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))}),window.addEventListener("keydown",function(o){27===o.keyCode&&popup.classList.contains("modal-show")&&(popup.classList.remove("modal-show"),popup.classList.remove("modal-error"))});
